import React,{Component} from "react";
import * as THREE from 'three';
class Testthree extends Component{

    cube =null;

    componentDidMount(){

        //scene -allow you to set up what and where is to be rendered by three. js

        this.scene = new THREE.Scene();

        //renderer - displays the scene onto a HTML Canvas Element

        this.renderer =new THREE.WebGLRenderer();
        this.renderer.setSize(window.innerWidth,window.innerHeight)


        this.mount.appendChild(this.renderer.domElement)

        //camera - gives a 3d view where things in the distance appear smaller than things up close

        this.camera = new THREE.PerspectiveCamera(75,window.innerWidth/window.innerHeight,0.1,1000)
        this.camera.position.z=5;

        var geometry = new THREE.BoxGeometry(1,1,1)
        var materail = new THREE.MeshBasicMaterial({
            color: 0x00ff00
        })
        var cube = new THREE.Mesh(geometry,materail)
        this.scene.add(cube)
        console.log("hai from main "+ this.cube)
        animation();
        this.renderer.render(this.scene,this.camera)

    }


    handleWindowResize =() =>{
        this.camera.aspect = window.innerWidth/ window.innerHeight
        this.camera.updateProjectionMatrix();
        this.renderer.setSize(window.innerWidth,window.innerHeight)
        this.renderer.render(this.scene,this.camera)
    }

    animation = () => {
        requestAnimationFrame(this.animation)
        // if(this.cube){
        this.cube.rotation.x += 0.01
        this.cube.rotation.y += 0.01
        // }
        // else{
        //     console.log(this.cube)
        // }

    }

    render(){
        return(
            <div ref={
                mount => {
                    this.mount=mount;

                }
            }>
            </div>
        )
    }
}
export default Testthree;
